import { DataTypes } from "sequelize";
import db from '../config/database'
const Email = db.define('em',{
    emailto:{
        type: DataTypes.STRING
    },
    title:{
        type: DataTypes.STRING
    },
    content:{
        type: DataTypes.STRING
    },
    userID:{
        type:DataTypes.INTEGER
    }

},{
    tableName:'em',
    timestamps: false
});

export default Email

