import { DataTypes} from "sequelize";
import db from '../config/database'

const Users = db.define('us', {
    email:{
        type: DataTypes.STRING
    },
    password:{
        type: DataTypes.STRING
    }
    },
    {
        tableName:'us',
        timestamps: false
    }
)

export default Users

